import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { API_URL } from "../../constants/api";
import styles from "./Form.module.scss";
interface Item {
  name: string;
  description: string;
  location: string;
  type: "Недвижимость" | "Авто" | "Услуги";
  image?: File;
  propertyType?: string;
  area?: number;
  rooms?: number;
  price?: number;
  brand?: string;
  model?: string;
  year?: number;
  mileage?: number;
  serviceType?: string;
  experience?: number;
  cost?: number;
  workSchedule?: string;
}

const Form: React.FC = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState<Item>({
    name: "",
    description: "",
    location: "",
    type: "Недвижимость",
  });
  const [file, setFile] = useState<File | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    // Преобразование числовые значения
    const newValue = ["area", "rooms", "price", "year", "mileage", "experience", "cost"].includes(name)
      ? Number(value)
      : value;

    setForm((prevForm) => ({
      ...prevForm,
      [name]: newValue,
    }));
  };

  // Загрузка фотографии объявления
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files[0];
    setFile(file);
  };

  // Отправка данных на сервер
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, form);
      navigate("/");
    } catch (error) {
      alert("Ошибка при создании объявления");
    }
  };

  return (
    <div className={styles.container}>
      <h1>Новое объявление</h1>
      <form onSubmit={handleSubmit} className={styles.form}>
        <label htmlFor='type'>Выберите категорию объявления</label>
            <select 
                id="type"
                name="type"
                value={form.type}
                onChange={handleChange}
            >
                <option value="Недвижимость">Недвижимость</option>
                <option value="Авто">Авто</option>
                <option value="Услуги">Услуги</option>
            </select>
            <label htmlFor="name">Название объявления</label>
            <input 
                id="name"
                type="text"
                name="name"
                value={form.name} 
                onChange={handleChange}
                required 
            />
            <label htmlFor="description">Описание объявления</label>
            <textarea
                id="description"
                name="description"
                value={form.description} 
                onChange={handleChange} 
                required 
            />
            <label htmlFor="location">Местоположение</label>
            <input 
                id="location"
                type="text" 
                name="location" 
                placeholder="Введите адрес" 
                value={form.location} 
                onChange={handleChange} 
                required 
            />
             <label htmlFor="image">Фотография</label>
            <input
                id="image"
                type="file"
                name="image"
                onChange={handleFileChange}
            />

            {/* Поля для Недвижимости */}
            {form.type === "Недвижимость" && (
            <>
            <label htmlFor="propertyType">Тип недвижимости</label>
                <input 
                    id="propertyType"
                    type="text" 
                    name="propertyType" 
                    value={form.propertyType || ""} 
                    onChange={handleChange} 
                    required />
            <label htmlFor="area">Площадь</label>
                <input 
                    id="area"
                    type="number" 
                    name="area" 
                    placeholder="кв.м" 
                    value={form.area || ""} 
                    onChange={handleChange} 
                    required />
                <label htmlFor="rooms">Количество комнат</label>
                <input 
                    id="rooms"
                    type="number" 
                    name="rooms" 
                    value={form.rooms || ""} 
                    onChange={handleChange} 
                    required />
                <input 
                    type="number" 
                    name="price" 
                    placeholder="Цена" 
                    value={form.price || ""} 
                    onChange={handleChange} 
                    required 
                />
            </>
            )}

            {/* Поля для Авто */}
            {form.type === "Авто" && (
            <>
                <input 
                    type="text" 
                    name="brand" 
                    placeholder="Марка" 
                    value={form.brand || ""} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="text" 
                    name="model" 
                    placeholder="Модель" 
                    value={form.model || ""} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="number" 
                    name="year" 
                    placeholder="Год выпуска" 
                    value={form.year || ""} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="number" 
                    name="mileage" 
                    placeholder="Пробег (км)" 
                    value={form.mileage || ""} 
                    onChange={handleChange} 
                    required 
                />
            </>
            )}

            {/* Поля для Услуг */}
            {form.type === "Услуги" && (
            <>
              <label htmlFor="serviceType">Тип услуги</label>
                <input 
                    id="serviceType"
                    type="text" 
                    name="serviceType" 
                    value={form.serviceType || ""} 
                    onChange={handleChange} 
                    required 
                />
                <label htmlFor="experience">Опыт</label>
                <input 
                    id="experience"
                    type="number" 
                    name="experience" 
                    placeholder="Количество лет" 
                    value={form.experience || ""} 
                    onChange={handleChange} 
                    required 
                />
                <label htmlFor="cost">Стоимость</label>
                <input 
                    id="cost"
                    type="number" 
                    name="cost" 
                    placeholder="руб." 
                    value={form.cost || ""} 
                    onChange={handleChange} 
                    required 
                />
                <label htmlFor="workSchedule">График работы</label>
                <input 
                    type="text" 
                    name="workSchedule" 
                    value={form.workSchedule || ""} 
                    onChange={handleChange} 
                    required 
                />
            </>
            )}

        <button type="submit">Создать</button>
      </form>
    </div>
  );
};

export default Form;
