import React, { useEffect, useState }  from 'react';
import { useNavigate } from 'react-router-dom'
import { API_URL } from '../../constants/api.js';
import SearchBar from '../../components/SearchBar/SearchBar';
import CategoryFilter from "../../components/CategoryFilter/CategoryFilter"
import Ads from '../../components/Ads/Ads';

import styles from './ListAds.module.scss';


interface Item {
    id: number;
    name: string;
    location: string;
    type: "Недвижимость" | "Авто" | "Услуги";
    image?: string; 
}

const ListAds: React.FC = () => {
    const navigate = useNavigate();
    const [items, setItems] = useState<Item[]>([]);
    const [selectedCategory, setSelectedCategory] = useState<string>("");
    const [searchItem, setSearchItem] = useState("")
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;

    // Загрузка данных с сервера
    useEffect(() => {
        fetch(API_URL)
            .then((res) => res.json())
            .then((data) => {
                console.log('Данные с сервера:', data);
                setItems(data)})
            .catch((error) => console.error(error))
    }, [])

    // Фильтрация по категории
    const filteredItems = selectedCategory
        ? items.filter((item) => item.type === selectedCategory) 
        : items;

    // Поиск по названию
    const searchedAds = searchItem
    ? filteredItems.filter((item) => item.name.toLowerCase().includes(searchItem.toLowerCase()))
    : filteredItems;

    // Пагинация
    const totalPages = Math.ceil(filteredItems.length / itemsPerPage);
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem =  indexOfLastItem - itemsPerPage;
    const currentItems = filteredItems.slice(indexOfFirstItem, indexOfLastItem);

    const handlePrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    return (
        <div className={styles.container}>
            <h1 className={styles.title}>Список объявлений</h1>
            <button onClick={() => navigate('/form')} className={styles.button_create}>Разместить объявление</button>

            <CategoryFilter
                categories={["Недвижимость", "Авто", "Услуги"]}
                selectedCategory={selectedCategory}
                onSelectCategory={setSelectedCategory}
             />
            <SearchBar 
                items={filteredItems} 
                onSearchItem={setSearchItem}
            />

            {filteredItems.length === 0 ? (
                <p>Нет объявлений в данной категории</p>
            ) : (
                <>
                    <ul className={styles.items_container}> 
                        {currentItems.map((item) => (
                            <Ads key={item.id} item={item}/>
                        ))}
                    </ul>
                    <div className={styles.pagination}>
                        <button
                            onClick={handlePrevPage}
                            disabled={currentPage === 1}>
                            Назад
                        </button>
                        <span>Страница {currentPage} of {totalPages} </span>
                        <button
                            onClick={handleNextPage}
                            disabled={currentPage === totalPages}>
                            Вперед
                        </button>
                    </div>
                </>
            )}
        </div>
    )
}
export default ListAds;
