import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import validationSchema from "../../components/Validation/Validation"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { Item } from "../../components/types/types";
import * as yup from "yup";
import { API_URL } from "../../constants/api";
import Button_return from "../../components/ButtonReturn/Button_return";
import styles from "./Form.module.scss";

const defaultValues: Partial<Item> = {
    name: "",
    description: "",
    location: "",
    type: "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å",
    image: "",
};

const Form: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isEditing = Boolean(location.state?.item);

   // –¢–µ–∫—É—â–∏–π —Ç–∏–ø –æ–±—ä—è–≤–ª–µ–Ω–∏—è
   const [selectedType, setSelectedType] = useState<string>(location.state?.item?.type || "");

// –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –≤–∞–ª–∏–¥–∞—Ü–∏–∏
  const {
    register,
    handleSubmit,
    setValue,
    watch,
    formState: { errors },
    reset,
  } = useForm<Item>({
    resolver: yupResolver(validationSchema(selectedType)),
    defaultValues: location.state?.item || defaultValues,
  });

  // üèÜ –°–ª–µ–¥–∏–º –∑–∞ –≤—ã–±–æ—Ä–æ–º —Ç–∏–ø–∞
  const type = watch("type");

  // ‚úÖ –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ö–µ–º—É –≤–∞–ª–∏–¥–∞—Ü–∏–∏ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ `type`
  useEffect(() => {
    if (type) {
      setSelectedType(type);
      reset({ type }); // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏—è –∏ –ø—Ä–∏–º–µ–Ω—è–µ–º –Ω–æ–≤—É—é —Å—Ö–µ–º—É
    }
  }, [type, reset]);
  // –ó–∞–ø–æ–ª–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–∏ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  useEffect(() => {
        if(location.state?.item) {
            Object.keys(location.state.item).forEach((key) => {
                setValue(key as keyof Item, location.state.item[key]);
            })
        }
  }, [location.state, setValue])

  // –û—Ç–ø—Ä–∞–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –Ω–∞ —Å–µ—Ä–≤–µ—Ä
  const onSubmit = async (data: Item) => {
    try {
        if (isEditing) {
            await axios.put(`${API_URL}/${location.state.item.id}`, data)
        } else {
            await axios.post(API_URL, data);
        }
      navigate("/list");
    } catch (error) {
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –æ–±—ä—è–≤–ª–µ–Ω–∏—è");
    }
  };


  console.log(errors);

  return (
    <div className={styles.container}>
        <div className={styles.container_title}>
            <Button_return />
            <h1>{isEditing ? "–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è" : "–ù–æ–≤–æ–µ –æ–±—ä—è–≤–ª–µ–Ω–∏–µ"}</h1>
        </div>
      <form onSubmit={handleSubmit(onSubmit)} className={styles.form}>
            <label htmlFor="name">–ù–∞–∑–≤–∞–Ω–∏–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è</label>
            <input 
                id="name"
                type="text"
                name="name"
                {...register("name")}
            />
            {errors.name && <p className={styles.error}>{errors.name.message}</p>}

            <label htmlFor="description">–û–ø–∏—Å–∞–Ω–∏–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è</label>
            <textarea
                id="description"
                name="description"
                {...register("description")}
            />
            {errors.description && <p className={styles.error}>{errors.description.message}</p>}

            <label htmlFor="location">–ú–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ</label>
            <input 
                id="location"
                type="text" 
                name="location" 
                placeholder="–í–≤–µ–¥–∏—Ç–µ –∞–¥—Ä–µ—Å" 
                {...register("location")}
            />
            {errors.location && <p className={styles.error}>{errors.location.message}</p>}

             <label htmlFor="image">–§–æ—Ç–æ–≥—Ä–∞—Ñ–∏—è</label>
            <input
                id="image"
                type="string"
                name="image"
                {...register("image")}
            />
            {errors.image && <p className={styles.error}>{errors.image.message}</p>}
             <label htmlFor='type'>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</label>
            <select 
                id="type"
                name="type"
                {...register("type")}
            >
                <option value="" disabled selected hidden>–í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é</option>
                <option value="–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å">–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å</option>
                <option value="–ê–≤—Ç–æ">–ê–≤—Ç–æ</option>
                <option value="–£—Å–ª—É–≥–∏">–£—Å–ª—É–≥–∏</option>
            </select>
            {errors.type && <p className={styles.error}>{errors.type.message}</p>}

            {/* –ü–æ–ª—è –¥–ª—è –ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç–∏ */}
            {selectedType === "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å" && (
            <>
            <label htmlFor="propertyType">–¢–∏–ø –Ω–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç–∏</label>
                <select 
                    id="propertyType"
                    name="propertyType" 
                    {...register("propertyType")}
                >
                    <option value="" disabled selected hidden>–í—ã–±–µ—Ä–∏—Ç–µ —Ç–∏–ø</option>
                    <option value="–ö–≤–∞—Ä—Ç–∏—Ä–∞">–ö–≤–∞—Ä—Ç–∏—Ä–∞</option>
                    <option value="–î–æ–º">–î–æ–º</option>
                    <option value="–ö–æ—Ç—Ç–µ–¥–∂">–ö–æ—Ç—Ç–µ–¥–∂</option>
                    <option value="–ö–æ–º–Ω–∞—Ç–∞">–ö–æ–º–Ω–∞—Ç–∞</option>
                    <option value="–ó–µ–º–µ–ª—å–Ω—ã–π —É—á–∞—Å—Ç–æ–∫">–ó–µ–º–µ–ª—å–Ω—ã–π —É—á–∞—Å—Ç–æ–∫</option>
                    <option value="–ì–∞—Ä–∞–∂">–ì–∞—Ä–∞–∂</option>
                    <option value="–ö–æ–º–º–µ—Ä—á–µ—Å–∫–∞—è –Ω–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å">–ö–æ–º–º–µ—Ä—á–µ—Å–∫–∞—è –Ω–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å</option>
                </select>
                {errors.propertyType && <p className={styles.error}>{errors.propertyType.message}</p>}
            <label htmlFor="area">–ü–ª–æ—â–∞–¥—å</label>
                <input 
                    id="area"
                    type="number" 
                    name="area" 
                    placeholder="–∫–≤.–º" 
                    {...register("area")}
                />
                {errors.area && <p className={styles.error}>{errors.area.message}</p>}
            <label htmlFor="rooms">–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–æ–º–Ω–∞—Ç</label>
                <input 
                    id="rooms"
                    type="number" 
                    name="rooms" 
                    {...register("rooms")}
                />
                {errors.rooms && <p className={styles.error}>{errors.rooms.message}</p>}
            <label htmlFor="price">–¶–µ–Ω–∞</label>
                <input 
                    id="price"
                    type="number" 
                    name="price" 
                    placeholder="—Ä—É–±" 
                    {...register("price")}
                />
                {errors.price && <p className={styles.error}>{errors.price.message}</p>}
            </>
            )}

            {/* –ü–æ–ª—è –¥–ª—è –ê–≤—Ç–æ */}
            {selectedType === "–ê–≤—Ç–æ" && (
            <>
            <label htmlFor="brand">–ú–∞—Ä–∫–∞</label>
                <select
                    id="brand"
                    name="brand" 
                    {...register("brand")} 
                >
                    <option value="" disabled selected hidden>–í—ã–±–µ—Ä–∏—Ç–µ –º–∞—Ä–∫—É</option>
                    <option value="Audi">Audi</option>
                    <option value="BMW">BMW</option>
                    <option value="Changan">Changan</option>
                    <option value="Chery">Chery</option>
                    <option value="Chevrolet">Chevrolet</option>
                    <option value="Daewo">Daewo</option>
                    <option value="Ford">Ford</option>
                </select>
                {errors.brand && <p className={styles.error}>{errors.brand.message}</p>}
            <label htmlFor="model">–ú–æ–¥–µ–ª—å</label>
                <input 
                    id="model"
                    type="text" 
                    name="model" 
                    {...register("model")}
                />
                {errors.model && <p className={styles.error}>{errors.model.message}</p>}
            <label htmlFor="year">–ì–æ–¥ –≤—ã–ø—É—Å–∫–∞</label>
                <input 
                    id="year"
                    type="number" 
                    name="year" 
                    {...register("year")}
                />
                {errors.year && <p>{errors.year.message}</p>}
                <label htmlFor="mileage">–ü—Ä–æ–±–µ–≥</label>
                <input 
                    id="mileage"
                    type="number" 
                    name="mileage" 
                    placeholder="–∫–º" 
                    {...register("mileage")}
                />
                {errors.mileage && <p className={styles.error}>{errors.mileage.message}</p>}
            </>
            )}

            {/* –ü–æ–ª—è –¥–ª—è –£—Å–ª—É–≥ */}
            {selectedType === "–£—Å–ª—É–≥–∏" && (
            <>
              <label htmlFor="serviceType">–¢–∏–ø —É—Å–ª—É–≥–∏</label>
                <input 
                    id="serviceType"
                    type="text" 
                    name="serviceType" 
                    {...register("serviceType")}
                />
                {errors.serviceType && <p className={styles.error}>{errors.serviceType.message}</p>}
                <label htmlFor="experience">–û–ø—ã—Ç</label>
                <input 
                    id="experience"
                    type="number" 
                    name="experience" 
                    placeholder="–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –ª–µ—Ç" 
                    {...register("experience")}
                />
                {errors.experience && <p className={styles.error}>{errors.experience.message}</p>}
                <label htmlFor="cost">–°—Ç–æ–∏–º–æ—Å—Ç—å</label>
                <input 
                    id="cost"
                    type="number" 
                    name="cost" 
                    placeholder="—Ä—É–±" 
                    {...register("cost")} 
                />
                {errors.cost && <p>{errors.cost.message}</p>}
                <label htmlFor="workSchedule">–ì—Ä–∞—Ñ–∏–∫ —Ä–∞–±–æ—Ç—ã</label>
                <input 
                    type="text" 
                    name="workSchedule" 
                    {...register("workSchedule")}
                />
                {errors.workSchedule && <p className={styles.error}>{errors.workSchedule.message}</p>}
            </>
            )}

        <button type="submit" className={styles.button_submit}>{isEditing ? "–û–±–Ω–æ–≤–∏—Ç—å" : "–°–æ–∑–¥–∞—Ç—å"}</button>
      </form>
    </div>
  );
};

export default Form;
