import React, { useEffect, useState }  from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../../constants/api.js';
import styles from './ListAds.module.scss';

interface Item {
    id: number;
    name: string;
    description: string;
    image?: string; 
}

const ListAds: React.FC = () => {
    const navigate = useNavigate();
    const [items, setItems] = useState<Item[]>([]);

    useEffect(() => {
        fetch(API_URL)
            .then((res) => res.json())
            .then((data) => {
                console.log('Данные с сервера:', data);
                setItems(data)})
            .catch((error) => console.error(error))
    }, [])

    return (
        <div className={styles.container}>
            <h1 className={styles.title}>Список объявлений</h1>
            <ul className={styles.items_container}> 
                {items.map((item) => (
                    <li key={item.id} className={styles.item}>
                        <h2>{item.name}</h2>
                        <p>{item.description}</p>
                        <img src={item.image || "https://www.laser-bulat.ru/upload/medialibrary/735/2lj6sel8ygv8p6j2xj85gplt9ufd5xpn.png" } alt='Фото' className={styles.item_photo} />
                        <button className={styles.button_open} onClick={() => navigate('/items/:id')}>Открыть</button>
                    </li>
                ))}
            </ul>
            <button onClick={() => navigate('/form')}> Создать объявление</button>
        </div>
    )
}
export default ListAds;
