import React, { useEffect, useState }  from 'react';
import { useNavigate } from 'react-router-dom'
import { API_URL } from '../../constants/api.js';
import SearchBar from '../../components/SearchBar/SearchBar';
import CategoryFilter from "../../components/CategoryFilter/CategoryFilter"
import Ads from '../../components/Ads/Ads';

import styles from './ListAds.module.scss';


interface Item {
    id: number;
    name: string;
    location: string;
    type: "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å" | "–ê–≤—Ç–æ" | "–£—Å–ª—É–≥–∏";
    image?: string; 
}

interface ListAdsProps {
    items?: Item[];
}

const ListAds: React.FC<ListAdsProps> = () => {
    const navigate = useNavigate();
    const [items, setItems] = useState<Item[]>([]);
    const [selectedCategory, setSelectedCategory] = useState<string>("");
    const [searchItem, setSearchItem] = useState("")
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;

    // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö —Å —Å–µ—Ä–≤–µ—Ä–∞
    useEffect(() => {
        fetch(API_URL)
            .then((res) => res.json())
            .then((data) => {
                console.log('–î–∞–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–∞:', data);
                setItems(data)})
            .catch((error) => console.error(error))
    }, [])

    // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
    const filteredItems = selectedCategory
        ? items.filter((item) => item.type === selectedCategory) 
        : items;

    // –ü–∞–≥–∏–Ω–∞—Ü–∏—è
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem =  indexOfLastItem - itemsPerPage;
    const currentItems = filteredItems.slice(indexOfFirstItem, indexOfLastItem);


    // –ü–æ–∏—Å–∫ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é
    const searchedAds = searchItem.length > 0
    ? filteredItems.filter((item) => item.name.toLowerCase().includes(searchItem.toLowerCase()))
    : currentItems;

    // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å—Ç—Ä–∞–Ω–∏—Ü
    const totalPages = Math.max(1, Math.ceil(filteredItems.length / itemsPerPage));

    // –°–±—Ä–æ—Å currentPage –ø—Ä–∏ —Å–º–µ–Ω–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
    useEffect(() => {
        setCurrentPage(1);
    }, [selectedCategory, searchItem]);

     // üîπ –û–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ currentPage, –µ—Å–ª–∏ —Å—Ç—Ä–∞–Ω–∏—Ü —Å—Ç–∞–ª–æ –º–µ–Ω—å—à–µ
     useEffect(() => {
        if (currentPage > totalPages) {
            setCurrentPage(totalPages);
        }
    }, [totalPages]);

    // –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å—Ç—Ä–∞–Ω–∏—Ü–∞–º–∏
    const handlePrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };
    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    return (
        <div className={styles.container}>
            <h1 className={styles.title}>–°–ø–∏—Å–æ–∫ –æ–±—ä—è–≤–ª–µ–Ω–∏–π</h1>
            <button onClick={() => navigate('/form')} className={styles.button_create}>–†–∞–∑–º–µ—Å—Ç–∏—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏–µ</button>

            <CategoryFilter
                categories={["–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å", "–ê–≤—Ç–æ", "–£—Å–ª—É–≥–∏"]}
                selectedCategory={selectedCategory}
                onSelectCategory={setSelectedCategory}
             />
            <SearchBar 
                items={filteredItems} 
                onSearchItem={setSearchItem}

            />
            {searchedAds && (
                    <ul className={styles.items_container}> 
                        {searchedAds.map((item) => (
                            <Ads key={item.id} item={item} />
                        ))}
                    </ul>
            )}

            <div className={styles.pagination}>
                <button
                    onClick={handlePrevPage}
                    disabled={currentPage === 1}>
                        –ù–∞–∑–∞–¥
                </button>
                <span>–°—Ç—Ä–∞–Ω–∏—Ü–∞ {currentPage} of {totalPages} </span>
                <button
                    onClick={handleNextPage}
                    disabled={currentPage === totalPages}>
                        –í–ø–µ—Ä–µ–¥
                </button>
            </div>
        </div>
    )
}
export default ListAds;
