import React, {  useEffect, useState }  from "react";
import Button_return from "../../components/Button_return/Button_return";
import { useParams, useLocation } from "react-router-dom";
import { API_URL } from '../../constants/api.js';
import styles from "./AdPage.module.scss";


interface Item {
    name: string;
    description: string;
    location: string;
    type: "Недвижимость" | "Авто" | "Услуги";
    image?: string;
    propertyType?: string;
    area?: number;
    rooms?: number;
    price?: number;
    brand?: string;
    model?: string;
    year?: number;
    mileage?: number;
    serviceType?: string;
    experience?: number;
    cost?: number;
    workSchedule?: string;
  }

const AdPage = () => {
    const { id } = useParams<{ id: string}>();
    const location = useLocation()
    const [item, setItem] = useState<Item | null>(null);

    useEffect(() => {
        if(location.state.item) {
            setItem(location.state.item);
        } else {
            fetch(`${API_URL}/${id}`)
            .then((res) => res.json())
            .then((data) => setItem(data))
            .catch((error) => console.error(error));
        }
    }, [id, location.state])

    if (!item) {
        return <div>Загрузка...</div>;
    }

    return (
        <div className={styles.container}>
            <Button_return />
            <div className={styles.item}>
                <img src={item.image || "https://www.laser-bulat.ru/upload/medialibrary/735/2lj6sel8ygv8p6j2xj85gplt9ufd5xpn.png" } alt='Фото' className={styles.item_photo} />
                    <div className={styles.item_text}>
                        <h2>{item.name}</h2>
                        <p>Местоположение: {item.location}</p>
                        <p>Тип: {item.type}</p>
                        <p>Описание: {item.description}</p>
                        {item.type === "Недвижимость" && (
                            <>
                                <p>{item.propertyType}</p>
                                <p>{item.area}</p>
                                <p>{item.rooms}</p>
                                <p>{item.price}</p>
                            </>
                        )}
                        {item.type === "Авто" && (
                            <>
                            <p>{item.brand}</p>
                            <p>{item.model}</p>
                            <p>{item.year}</p>
                            <p>{item.mileage}</p>
                            </>
                        )}
                        {item.type === "Услуги" && (
                            <>
                             <p>{item.serviceType}</p>
                             <p>{item.experience}</p>
                             <p>{item.cost}</p>
                             <p>{item.workSchedule}</p>
                            </>
                        )}

                    </div>
            </div>
        </div>
    )
}
export default AdPage;
