import React, { useState } from "react";
import styles from './SearchBar.module.scss';

interface Item {
    id: number;
    name: string;
    image?: string;
    location: string;
    type: "Недвижимость" | "Авто" | "Услуги";
  }

interface SearchBarProps {
    items: Item[]; 
    onSearchItem: (item: string) => void;
}

const SearchBar: React.FC<SearchBarProps> = ({ items, onSearchItem }) => {
    const [searchItem, setSearchItem] = useState("");
    const filteredItems = items.filter((item) =>
        item.name.toLowerCase().includes(searchItem.toLowerCase())
    );
    const handleResetSearch = () => {
        setSearchItem("")
    }

    return (
        <div className={styles.container}>
            <label htmlFor="search">Поиск объявления по названию</label>
            <input 
                type="text"
                id="search"
                value={searchItem}
                className={styles.search_input}
                placeholder="Введите название..."
                onChange={(e) => setSearchItem(e.target.value)}
            />
            {searchItem && (
                <button onClick={handleResetSearch}>Очистить</button>
            )}
            {searchItem.length > 1 && (
            <ul>
                {filteredItems.map((item) => (
                    <li 
                        key={item.id} 
                        onClick={() => onSearchItem(item.name)}
                        className={styles.item_container}
                    >
                        <img src={item.image || "https://www.laser-bulat.ru/upload/medialibrary/735/2lj6sel8ygv8p6j2xj85gplt9ufd5xpn.png" } alt='Фото' className={styles.item_photo} />
                        {item.name}
                        <p>{item.location}</p>
                        <p>{item.type}</p>
                    </li>
                ))}
            </ul>
            )}
        </div>
    )
}

export default SearchBar;
