import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { API_URL } from "../../constants/api";

interface Item {
  name: string;
  description: string;
  location: string;
  type: "Недвижимость" | "Авто" | "Услуги";
  propertyType?: string;
  area?: number;
  rooms?: number;
  price?: number;
  brand?: string;
  model?: string;
  year?: number;
  mileage?: number;
  serviceType?: string;
  experience?: number;
  cost?: number;
  workSchedule?: string;
}

const Form: React.FC = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState<Item>({
    name: "",
    description: "",
    location: "",
    type: "Недвижимость",
  });
  const [file, setFile] = useState<File | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    // Преобразование числовые значения
    const newValue = ["area", "rooms", "price", "year", "mileage", "experience", "cost"].includes(name)
      ? Number(value)
      : value;

    setForm((prevForm) => ({
      ...prevForm,
      [name]: newValue,
    }));
  };

// Обработка загрузки файла
const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!e.target.files || e.target.files.length === 0) return;
    setFile(e.target.files[0]);
  };

  // Отправка данных на сервер
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", form.name);
    formData.append("description", form.description);
    formData.append("location", form.location);
    formData.append("type", form.type);

    if (file) {
      formData.append("image", file); // Добавляем изображение
    }

    try {
      await axios.post(API_URL, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      navigate("/");
    } catch (error) {
      alert("Ошибка при создании объявления");
    }
  };


  return (
    <div>
      <h1>Создать объявление</h1>
      <form onSubmit={handleSubmit}>
        <input 
            type="text"
            name="name" 
            placeholder="Название" 
            value={form.name} 
            onChange={handleChange}
            required 
        />
        <textarea
            name="description"
            placeholder="Описание" 
            value={form.description} 
            onChange={handleChange} 
            required 
        />
        <input 
            type="text" 
            name="location" 
            placeholder="Местоположение" 
            value={form.location} 
            onChange={handleChange} 
            required 
        />
        <select 
            name="type" 
            value={form.type}
            onChange={handleChange}
        >
          <option value="Недвижимость">Недвижимость</option>
          <option value="Авто">Авто</option>
          <option value="Услуги">Услуги</option>
        </select>
        <input
            type="file"
            name="image"
            onChange={handleFileChange}
        />

        {/* Поля для Недвижимости */}
        {form.type === "Недвижимость" && (
          <>
            <input 
                type="text" 
                name="propertyType" 
                placeholder="Тип недвижимости" 
                value={form.propertyType || ""} 
                onChange={handleChange} 
                required />
            <input 
                type="number" 
                name="area" 
                placeholder="Площадь (кв.м)" 
                value={form.area || ""} 
                onChange={handleChange} 
                required />
            <input 
                type="number" 
                name="rooms" 
                placeholder="Количество комнат" 
                value={form.rooms || ""} 
                onChange={handleChange} 
                required />
            <input 
                type="number" 
                name="price" 
                placeholder="Цена" 
                value={form.price || ""} 
                onChange={handleChange} 
                required 
            />
          </>
        )}

        {/* Поля для Авто */}
        {form.type === "Авто" && (
          <>
            <input 
                type="text" 
                name="brand" 
                placeholder="Марка" 
                value={form.brand || ""} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="text" 
                name="model" 
                placeholder="Модель" 
                value={form.model || ""} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="number" 
                name="year" 
                placeholder="Год выпуска" 
                value={form.year || ""} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="number" 
                name="mileage" 
                placeholder="Пробег (км)" 
                value={form.mileage || ""} 
                onChange={handleChange} 
                required 
            />
          </>
        )}

        {/* Поля для Услуг */}
        {form.type === "Услуги" && (
          <>
            <input 
                type="text" 
                name="serviceType" 
                placeholder="Тип услуги" 
                value={form.serviceType || ""} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="number" 
                name="experience" 
                placeholder="Опыт (лет)" 
                value={form.experience || ""} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="number" 
                name="cost" 
                placeholder="Стоимость услуги" 
                value={form.cost || ""} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="text" 
                name="workSchedule" 
                placeholder="График работы" 
                value={form.workSchedule || ""} 
                onChange={handleChange} 
                required 
            />
          </>
        )}

        <button type="submit">Создать</button>
      </form>
    </div>
  );
};

export default Form;
